import React, { useEffect, useState } from 'react';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import axios from "axios";

const rootStyle = {
    flexGrow: 1,
}

const appbarStyle = {
    backgroundColor: '#ED1C16',
}

const titleStyle = {
    flexGrow: 1,
}

const styles = {
    root: {
      display: 'flex',
      minHeight: '100vh',
    },
    drawer: {
      [theme.breakpoints.up('sm')]: {
        width: drawerWidth,
        flexShrink: 0,
      },
    },
    app: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
    },
    main: {
      flex: 1,
      padding: theme.spacing(6, 4),
      background: '#eaeff1',
    },
    footer: {
      padding: theme.spacing(2),
      background: '#eaeff1',
    },
  };

function Landing(props) {
    const { classes } = props;
    const initialUserState = {
        loggedIn: false,
        session: {
            AccountID: 0,
            LastActivityTime: "",
        },
    };

    // Getter and setter for user state
    const [session, setSession] = useState(initialUserState)

    useEffect(() => {
        const getSession = async () => {
            axios.get(
                "http://localhost:3001/v1/sessions",
                { withCredentials: true }
            ).then(response => {
                console.log(response);
                if (response.error) {
                    console.log('Should not print error');
                    return
                }
                if (response.status === 200) {
                    setSession({
                        loggedIn: true,
                        session: response.data,
                    });
                }
            });
        }

        getSession()
    }, [])


    return (
        <React.Fragment>
            <div style={rootStyle}>
                <AppBar position="static" style={appbarStyle}>
                    <Toolbar>
                        <Typography variant="h6" style={titleStyle}>Hmm</Typography>
                        {
                            session.loggedIn ?
                                <div>
                                    <Button component={Link} to={'/me'} color="inherit">My account</Button>
                                    <Button component={Link} to={'/logout'} color="inherit">Logout</Button>
                                </div> :
                                <Button component={Link} to={'/login'} color="inherit">Login</Button>
                        }
                    </Toolbar>
                </AppBar>
            </div>
        </React.Fragment >
    );
}

export default withStyles(styles)(Landing);
