import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import axios from "axios";
import Paperbase from '../Dashboard/Paperbase';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    appbar: {
        backgroundColor: '#ED1C16',
    },
    title: {
        flexGrow: 1,
    },
}));

function Landing() {
    const initialUserState = {
        session: {},
        loading: true,
    }

    const [loading, setLoading] = useState(true)
    const [session, setSession] = useState({})
    useEffect(() => {
        const getSession = async () => {
            const { response } = await axios.get(`http://localhost:3001/v1/sessions`, { withCredentials: true })
            console.log(response);

            if (response.status === 200) {
                console.log('Yes');
            } else if (response.status === 401) {
                console.log('Nope');
            }
            setSession(response)
            setLoading(false)
        }
        getSession()
    }, [])

    const classes = useStyles();
    return (
        <React.Fragment>
            <div className={classes.root}>
                <AppBar position="static" className={classes.appbar}>
                    <Toolbar>
                        <Typography variant="h6" className={classes.title}>Hmm</Typography>
                        {
                            loading ?
                            <Button component={Link} to={'/login'} color="inherit">Login</Button> :
                            <Button component={Link} to={'/dashboard'} color="inherit">dashboard</Button>
                        }

                    </Toolbar>
                </AppBar>
            </div>
        </React.Fragment>
    );
}

export default Landing;
