import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import axios from "axios";

const rootStyle = {
    flexGrow: 1,
}

const appbarStyle = {
    backgroundColor: '#ED1C16',
}

const titleStyle = {
    flexGrow: 1,
}

function Landing() {

    const initialUserState = {
        loggedIn: false,
        session: {
            AccountID: 0,
            LastActivityTime: "",
