                console.log(response);
                if (response.error) {
                    console.log('Should not print error');
                    return
                }
                if (response.status === 200) {
                    this.setState({
                        loggedIn: true,
                        session: response.data,
                    });
                }
            });
        }

        getSession()
    }, [])


