import React, { Component } from "react";
import Landing from "./Landing/Landing";
import Paperbase from './Dashboard/Paperbase';
import axios from "axios";

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      loggedIn: false,
      user: {}
    };

    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.loggedInCheck = this.loggedInCheck.bind(this);
  }

  loggedInCheck(data) {
    axios
      .get(
        "http://localhost:3001/v1/sessions",
        { withCredentials: true }
      )
      .then(response => {
        if (response.status === 200) {
          log.console(response);
          this.setState({
            loggedIn: true,
            user: data.user
          });
          this.props.history.push('/Dashboard');
        }
      })
      .catch(error => {
        console.log("login error", error);
      });
  }

  handleLogout() {
    this.setState({
      loggedIn: false,
      user: {}
    });
  }

  handleLogin(data) {
    this.setState({
      loggedIn: true,
      user: data.user
    });
  }

  render() {
    return (
      <div className="app">
        {
          this.loggedIn ?
            <Paperbase />
            :
            <Landing />
        }
      </div>
    );
  }
}
