import React, { Component } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import axios from "axios";

const rootStyle = {
    flexGrow: 1,
}

const appbarStyle = {
    backgroundColor: '#ED1C16',
}

const titleStyle = {
    flexGrow: 1,
}

export default class Landing extends Component {
    // constructor(props) {
    //     super(props);

    //     this.state = {
    //         session: "initial value",
    //     };
    // }
    state = {
        session: "initial value",
    };

    componentDidMount() {
        axios.get(
            "http://localhost:3001/v1/sessions",
            { withCredentials: true }
        ).then(response => {
            if (response.status === 200) {
                this.setState({
                    session: "response.data",
                });
                console.log(response);
                console.log(this.state.session);
            }
        });
    }

    render() {
        return (
            <React.Fragment>
                <div style={rootStyle}>
                    <AppBar position="static" style={appbarStyle}>
                        <Toolbar>
                            <Typography variant="h6" style={titleStyle}>Hmm</Typography>
                            {
                                this.session ?
                                    <Button component={Link} to={'/login'} color="inherit">Login</Button> :
                                    <Button component={Link} to={'/dashboard'} color="inherit">My account</Button>
                            }
                        </Toolbar>
                    </AppBar>
                    session data: {this.state.session}
                </div>
            </React.Fragment >
        );
    }
}
